import React, {useState} from "react";
import Header from './components/header/header';
import './Login.css';
import View from './images/view_pass.svg';

function Login(){
    const [loginId, setloginId] = useState("");
    const [password, setPassword] = useState("");

    function validateForm() {
        return loginId.length > 0 && password.length > 0;
    }

    async function login() {
        console.log("hello")
        let item = {loginId, password};
        let result = await fetch("https://api.glasswing.in/api/loginv",{
        method: 'POST',
        headers:{ 
            'Content-Type': 'application/json',
            'Accept': 'application/json'
    },
    body: JSON.stringify(item)
    });
    result = await result.json();
    localStorage.setItem(JSON.stringify("user-info",result)) 
    console.log(result, item, JSON.stringify("user-info",result));
    }

     return(
         <div>
             <div className="mainContainer">
                 <Header/>
                <div className="row justify-content-md-center">
                    <div className="login">
                    <button className="officeLogin" onClick={null}>
                        Login in using Microsoft Office
                    </button>
                <div>Or Use E-mail / Mobile</div>
                <form onSubmit={login} >
                    <input type="text" name="email_mobile" placeholder="Email or Mobile" value={loginId} required onChange={(e) => setloginId(e.target.value)} />
                    <br/>
                    <input type="password" name="password" placeholder="Password" value={password} required onChange={(e) => setPassword(e.target.value)}/>
                    {/* <img  src={View} alt="pass icon" class="ng-tns-c123-2 passIcon"/> */}
                    <br/>
                    <button onClick={login} type="submit" value="Submit"> Submit </button>
                </form>
                    </div>
                </div>
            </div>
         </div>
     )
}


export default Login;
